
import json
import os

def load_seed(filename):
    if os.path.exists(filename):
        with open(filename, 'r') as f:
            try:
                return json.load(f)
            except json.JSONDecodeError:
                return []
    return []

def merge_data(existing_list, new_list, id_field):
    # Create a dict from existing data
    data_map = {item[id_field]: item for item in existing_list}
    # Update/Add new items
    for item in new_list:
        data_map[item[id_field]] = item
    # Return sorted list
    return sorted(data_map.values(), key=lambda x: x[id_field])

